(725, 95, '_como-funciona_1_icone', 'field_5b93bd373a1fd'),
(726, 95, 'como-funciona_1_titulo', 'Proposta Comercial e Contrato'),
(727, 95, '_como-funciona_1_titulo', 'field_5b93bd8a3a1fe'),
(728, 95, 'como-funciona_1_texto', 'Com o aceite da Proposta Comercial, a CertOrgânico envia o Contrato de Prestação de Serviço e demais documentos\r\nlegais para assinatura e formalização do mesmo.'),
(729, 95, '_como-funciona_1_texto', 'field_5b93bd9b3a1ff'),
(730, 95, 'como-funciona_1_largura', '12'),
(731, 95, '_como-funciona_1_largura', 'field_5b93bdb03a200'),
